package VariablesAndDataTypes;

public class datatypeOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//premitive variable types
		
		int var1=277777778; //integer can store only this much value
		
		long var2 = 22222222222222L;//you need to write capital L at the end of the number for some reason
		float var3 = 3.14f; //user need to enter f at the end of it
		double var4 = 3.14567; //generally double is used more often than float because people don't want to use the f at the end
		char symbol = '@';  //limited symbols are given to the data type char and @ is one of them
		String name = "swapnil"; //string is a reference data type and not an premitive data type
		
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(var3);
		System.out.println(var4);
		System.out.println(symbol);
		System.out.println(name);
		

	}

}
